sys_script_email.script:
(function runMailScript(
    /* GlideRecord */
    current,
    /* TemplatePrinter */
    template,
    /* Optional EmailOutbound */
    email,
    /* Optional GlideRecord */
    email_action,
    /* Optional GlideRecord */
    event
) {

    // Query to find all NeedIt Task records with due date field values in the past for the same Assigned to & not in Closed State (3)
    var now = new GlideDateTime();
    var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');

    overdueNITasks.addQuery('due_date', '<=', now);
    overdueNITasks.addQuery('state', '<', 3);
    overdueNITasks.addQuery('assigned_to', current.assigned_to);
    overdueNITasks.query();

    var queryRowCount = overdueNITasks.getRowCount();

    if (queryRowCount > 1) {
        // Print the the Message field on a notification
        template.print('In addition to this overude NeedIt Task, you have ' + (queryRowCount - 1) + ' more overdue NeedIt Tasks.');
    }

})(current, template, email, email_action, event);
